import React, { Component } from "react";
import LoginPage from "./LoginPage";
import HomePage from "./HomePage";
class Conditional_Rendering extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isLogin: true,
    };
  }

  render() {
    
    const {isLogin}=this.state
    let element;
    if (isLogin) {
       element= <HomePage />;
    } else {
       element= <LoginPage />;
    }

return(
<div>
{element}

</div>

)




  }
}
export default Conditional_Rendering;
